import React from "react";
import {capitalizeFirstLetter} from "../utils/helpers"

function Nav(props) {
    const {
        pages = [],
        setCurrentPage,
        currentPage
    } = props;
    return (
        <nav class>
        <ul className="flex-row">
          {pages.map((Page) => (
            <li
              className={`mx-5 ${
                currentPage.name === Page.name && 'navActive'
                }`}
              key={Page.name}
            >
              <span
                onClick={() => setCurrentPage(Page)}
              >
                {capitalizeFirstLetter(Page.name)}
              </span>
            </li>
          ))}
        </ul>
      </nav>
    );
}

export default Nav;
